The following statement contains a magic number: if (!info.LastIP.Equals (System.Net.IPAddress.None)) {  	// Show alts  	List<PlayerInfo> altNames = new List<PlayerInfo> ();  	int bannedAltCount = 0;  	foreach (PlayerInfo playerFromSameIP in PlayerDB.FindPlayers (info.LastIP)) {  		if (playerFromSameIP == info)  			continue;  		altNames.Add (playerFromSameIP);  		if (playerFromSameIP.IsBanned) {  			bannedAltCount++;  		}  	}  	// Stats  	if (info.BlocksDrawn > 500000000) {  		textBox1.Text += String.Format ("  Built {0} and deleted {1} blocks' drew {2}M blocks' wrote {3} messages.
