"' info.BlocksBuilt' info.BlocksDeleted' info.BlocksDrawn / 1000' info.MessagesWritten);  	}  	else if (info.BlocksDrawn > 0) {  		textBox1.Text += String.Format ("  Built {0} and deleted {1} blocks' drew {2} blocks' wrote {3} messages.
"' info.BlocksBuilt' info.BlocksDeleted' info.BlocksDrawn' info.MessagesWritten);  	}  	else {  		textBox1.Text += String.Format ("  Built {0} and deleted {1} blocks' wrote {2} messages.
"' info.BlocksBuilt' info.BlocksDeleted' info.MessagesWritten);  	}  	// More stats  	if (info.TimesBannedOthers > 0 || info.TimesKickedOthers > 0 || info.PromoCount > 0) {  		textBox1.Text += String.Format ("  Kicked {0}' Promoted {1} and banned {2} players.
"' info.TimesKickedOthers' info.PromoCount' info.TimesBannedOthers);  	}  	if (info.TimesKicked > 0) {  		if (info.LastKickDate != DateTime.MinValue) {  			textBox1.Text += String.Format ("  Got kicked {0} times. Last kick {1} ago by {2}
"' info.TimesKicked' info.TimeSinceLastKick.ToMiniString ()' info.LastKickByClassy);  		}  		else {  			textBox1.Text += String.Format ("  Got kicked {0} times.
"' info.TimesKicked);  		}  		if (info.LastKickReason != null) {  			textBox1.Text += String.Format ("  Kick reason: {0}
"' info.LastKickReason);  		}  	}  	// Promotion/demotion  	if (info.PreviousRank == null) {  		if (info.RankChangedBy == null) {  			textBox1.Text += String.Format ("  Rank is {0}&S (default).
"' info.Rank.ClassyName);  		}  		else {  			textBox1.Text += String.Format ("  Promoted to {0}&S by {1}&S {2} ago.
"' info.Rank.ClassyName' info.RankChangedByClassy' info.TimeSinceRankChange.ToMiniString ());  			if (info.RankChangeReason != null) {  				textBox1.Text += String.Format ("  Promotion reason: {0}
"' info.RankChangeReason);  			}  		}  	}  	else if (info.PreviousRank <= info.Rank) {  		textBox1.Text += String.Format ("  Promoted from {0}&S to {1}&S by {2}&S {3} ago.
"' info.PreviousRank.ClassyName' info.Rank.ClassyName' info.RankChangedByClassy' info.TimeSinceRankChange.ToMiniString ());  		if (info.RankChangeReason != null) {  			textBox1.Text += String.Format ("  Promotion reason: {0}
"' info.RankChangeReason);  		}  	}  	else {  		textBox1.Text += String.Format ("  Demoted from {0}&S to {1}&S by {2}&S {3} ago.
"' info.PreviousRank.ClassyName' info.Rank.ClassyName' info.RankChangedByClassy' info.TimeSinceRankChange.ToMiniString ());  		if (info.RankChangeReason != null) {  			textBox1.Text += String.Format ("  Demotion reason: {0}
"' info.RankChangeReason);  		}  	}  	if (!info.LastIP.Equals (System.Net.IPAddress.None)) {  		// Time on the server  		TimeSpan totalTime = info.TotalTime;  		if (info != null) {  			totalTime = totalTime.Add (info.TimeSinceLastLogin);  		}  		textBox1.Text += String.Format ("  Spent a total of {0:F1} hours ({1:F1} minutes) here.
"' totalTime.TotalHours' totalTime.TotalMinutes);  	}  	textBox1.Text = Color.StripColors (textBox1.Text);  }

